CREATE OR REPLACE PROCEDURE alterar_dt_envio_prot ( nr_seq_protocolo_p bigint, nr_protocolo_p text, ds_arquivo_envio_p text, ie_atualiza_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_protocolo_p IS NOT NULL AND nr_protocolo_p::text <> '') then 
 
	if (ie_atualiza_p <> 'S') then 
		begin 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
		nm_usuario_envio = nm_usuario_p, 
		ds_arquivo_envio = ds_arquivo_envio_p 
		where	nr_protocolo = nr_protocolo_p 
		and	nr_seq_protocolo = nr_seq_protocolo_p;
		end;
		 
	elsif (ie_atualiza_p = 'S') then 
		begin 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
		dt_entrega_convenio = clock_timestamp(), 
		nm_usuario_envio = nm_usuario_p, 
		ds_arquivo_envio = ds_arquivo_envio_p 
		where	nr_protocolo = nr_protocolo_p 
		and	nr_seq_protocolo = nr_seq_protocolo_p;
		end;	
		 
	end if;
	 
end if;		
commit;
 
end;
