CREATE OR REPLACE PROCEDURE alterar_dt_fim_execut ( cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_dt_fim	bigint;

BEGIN

if (cd_empresa_p IS NOT NULL AND cd_empresa_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_dt_fim
	from	checkup_imp_pessoa
	where	cd_empresa_ref = cd_empresa_p
	and	coalesce(dt_fim::text, '') = '';

	if (qt_dt_fim > 0) then
		begin
		update	checkup_imp_pessoa
		set	dt_fim	= clock_timestamp()
		where	cd_empresa_ref = cd_empresa_p
		and	coalesce(dt_fim::text, '') = '';
		commit;
		end;
	end if;
	end;
end if;
commit;

end;
