CREATE OR REPLACE PROCEDURE alterar_dt_fim_exec_proj_rec ( nm_usuario_p text, nr_sequencia_p bigint, ie_liberar_p text) AS $body$
BEGIN


if (ie_liberar_p = 'S') then
	update	projeto_recurso
	set 	dt_fim_exec = clock_timestamp(),
		nm_usuario_fim_exec = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_sequencia_p;
else if (ie_liberar_p = 'N') then
	update	projeto_recurso
	set 	dt_fim_exec  = NULL,
		nm_usuario_fim_exec  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_sequencia_p;
	end if;
end if;

commit;

end;
$body$
