SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_dt_fim_real_cron ( nm_usuario_p text) AS $body$
BEGIN

/*
	Está procedure irá verificar todos os cronogramas que estão com o percentual de conclusão como 100% mas não foi inserido uma data para o campo "  Término real"  (dt_fim_real)
*/
update	proj_cronograma c
set    	c.dt_fim_real = (SELECT trunc(max(xx.dt_fim_real))
						from   proj_cron_etapa xx
						where  xx.nr_seq_cronograma = c.nr_sequencia),
nm_usuario = nm_usuario_p
where	c.nr_sequencia in (	select d.nr_sequencia
							from 	proj_cronograma d,
									proj_projeto p
							where 	d.nr_seq_proj = p.nr_sequencia
							and 	coalesce(d.dt_fim_real::text, '') = ''
							and    	d.ie_situacao = 'A'
							and    	d.ie_classificacao = 'D'
							and	   	d.pr_realizacao = 100
							and    	d.dt_inicio > to_date('01/06/2016', 'DD/MM/YYYY'));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_dt_fim_real_cron ( nm_usuario_p text) FROM PUBLIC;

