CREATE OR REPLACE PROCEDURE alterar_dt_partic_cirurgia ( nr_cirurgia_p bigint, dt_termino_p timestamp, dt_inicio_real_p timestamp) AS $body$
DECLARE

dt_entrada_w timestamp;
dt_saida_w   timestamp;
nr_sequencia_w bigint;
nm_usuario_w varchar(15);

c01 CURSOR FOR
SELECT       nr_sequencia,
		   dt_entrada,
		   dt_saida,
		   nm_usuario
from cirurgia_participante
where        nr_cirurgia = nr_cirurgia_p;


BEGIN
  open c01;
  loop
  fetch c01 into
       nr_sequencia_w,
       dt_entrada_w,
       dt_saida_w,
       nm_usuario_w;
  EXIT WHEN NOT FOUND; /* apply on c01 */
       begin
       if (coalesce(dt_entrada_w::text, '') = '') and (coalesce(dt_saida_w::text, '') = '') and (dt_termino_p IS NOT NULL AND dt_termino_p::text <> '')      then
               update	cirurgia_participante
               set     	dt_entrada 	= dt_inicio_real_p,
                        dt_saida	= dt_termino_p
               where   	nr_sequencia 	= nr_sequencia_w
	       and	nr_cirurgia	= nr_cirurgia_p;
               commit;
       elsif (dt_entrada_w IS NOT NULL AND dt_entrada_w::text <> '') and (coalesce(dt_saida_w::text, '') = '')  and (dt_termino_p IS NOT NULL AND dt_termino_p::text <> '') then
               update  	cirurgia_participante
               set     	dt_saida   	= dt_termino_p
               where	nr_sequencia	= nr_sequencia_w
	       and	nr_cirurgia	= nr_cirurgia_p;
               commit;
       end if;
       end;
  end loop;
  close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
