CREATE OR REPLACE PROCEDURE alterar_dt_pls_pfpj_incon ( cd_pessoa_fisica_p text, cd_cgc_p text, nr_sequencia_p bigint, ds_email_p text, ds_msg_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ds_msg_w	varchar(4000);

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		begin
		update	pls_pessoa_inconsistente
		set	dt_envio_email		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_lote		= nr_sequencia_p
		and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
		end;
	elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		begin
		update	pls_pessoa_inconsistente
		set	dt_envio_email	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_seq_lote	= nr_sequencia_p
		and	cd_cgc		= cd_cgc_p;
		end;
	end if;

	ds_msg_w := obter_texto_dic_objeto(82274, wheb_usuario_pck.get_nr_seq_idioma, 'DS_DESTINO=' || ds_email_p || ';');

	end;
end if;
ds_msg_p	:= ds_msg_w;
commit;
end;
