CREATE OR REPLACE PROCEDURE alterar_dt_susp_proj_rec ( nm_usuario_p text, nr_sequencia_p bigint, ie_suspender_p text) AS $body$
BEGIN

if (ie_suspender_p = 'S') then
	update	projeto_recurso
	set 	dt_suspensa = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nm_usuario_suspensao = nm_usuario_p
	where  	nr_sequencia = nr_sequencia_p;
elsif (ie_suspender_p = 'N') then
	update	projeto_recurso
	set 	dt_suspensa  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nm_usuario_suspensao  = NULL
	where  	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
