CREATE OR REPLACE PROCEDURE alterar_dt_validade_usuario ( nm_usuario_param_p text, dt_validade_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update	usuario
	set	dt_validade_usuario = dt_validade_p,
		nm_usuario_atual = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nm_usuario = nm_usuario_param_p;

end if;

commit;

end;
$body$
