CREATE OR REPLACE PROCEDURE alterar_dt_vencimento_cheque ( nr_seq_cheque_p bigint, dt_vencimento_atual_p timestamp, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (dt_vencimento_atual_p IS NOT NULL AND dt_vencimento_atual_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update	cheque_cr
	set    	dt_vencimento_atual = trunc(dt_vencimento_atual_p,'dd'),
		dt_atualizacao = clock_timestamp(),
		nm_usuario     = nm_usuario_p
	where  	nr_seq_cheque = nr_seq_cheque_p;

	commit;

end if;

end;
$body$
