CREATE OR REPLACE PROCEDURE alterar_escala_horario ( nr_seq_escala_p bigint, nm_usuario_p text, ie_dia_semana_p text, dt_inicial_p timestamp, dt_final_p timestamp, nr_sequencia_p bigint) AS $body$
