CREATE OR REPLACE PROCEDURE alterar_estab_conta_paciente (nr_interno_conta_p bigint, cd_estabelecimento_dest_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_atendimento_w		bigint;
nr_seq_protocolo_w		bigint;
qt_contas_w			bigint;
cd_estab_conta_w		smallint;
cd_estab_atend_w		smallint;
ie_estab_adiant_w		varchar(1);


BEGIN 
 
select	nr_atendimento, 
	nr_seq_protocolo, 
	coalesce(cd_estabelecimento,0) 
into STRICT	nr_atendimento_w, 
	nr_seq_protocolo_w, 
	cd_estab_conta_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select	coalesce(max(cd_estabelecimento),0) 
into STRICT	cd_estab_atend_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_w;
 
if (cd_estab_conta_w = cd_estab_atend_w) then 
	select	count(*) 
	into STRICT	qt_contas_w 
	from	conta_paciente 
	where	nr_atendimento		= nr_atendimento_w 
	and	cd_estabelecimento	= cd_estab_conta_w;
else 
	select	count(*) 
	into STRICT	qt_contas_w 
	from	conta_paciente 
	where	nr_atendimento		= nr_atendimento_w;
end if;
 
if (coalesce(nr_seq_protocolo_w::text, '') = '') then 
	begin 
 
	update	conta_paciente 
	set	cd_estabelecimento	= cd_estabelecimento_dest_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_p;
 
	if (qt_contas_w = 1) then 
		 
		if (coalesce(Obter_Valor_Param_Usuario(67,278,obter_perfil_ativo,nm_usuario_p,0),'S')='S') then 
			begin 
			update	atendimento_paciente 
			set	cd_estabelecimento	= cd_estabelecimento_dest_p, 
				dt_atualizacao		= clock_timestamp(), 
				nm_usuario		= nm_usuario_p 
			where	nr_atendimento		= nr_atendimento_w;
			end;
		end if;
		 
		ie_estab_adiant_w := obter_param_usuario(67, 687, obter_perfil_ativo, nm_usuario_p, cd_estab_conta_w, ie_estab_adiant_w);
		 
		if (coalesce(ie_estab_adiant_w,'N') = 'S') then 
			begin 
			update	adiantamento 
			set	cd_estabelecimento = cd_estabelecimento_dest_p, 
				dt_atualizacao   = clock_timestamp(), 
				nm_usuario     = nm_usuario_p 
			where	nr_atendimento   = nr_atendimento_w;
			end;
		end if;
 
	end if;
	 
	end;
end if;
 
 
if (coalesce(Obter_Valor_Param_Usuario(67,396,obter_perfil_ativo,nm_usuario_p,0),'S')='S') then 
	begin 
	CALL atualiza_prestador_conpaci(nr_interno_conta_p,nm_usuario_p);
	end;
end if;
 
if (coalesce(Obter_Valor_Param_Usuario(67,492,obter_perfil_ativo,nm_usuario_p,0),'S')='S') then 
	begin 
	CALL atualiza_prestador_matpaci(nr_interno_conta_p,nm_usuario_p);
	end;
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
