CREATE OR REPLACE PROCEDURE alterar_estab_orcamento ( nr_sequencia_orcamento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_consiste_regra_plano_w	varchar(10);


BEGIN

ie_consiste_regra_plano_w := Obter_Param_Usuario(106, 28, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_regra_plano_w);

update	orcamento_paciente
set	cd_estabelecimento = cd_estabelecimento_p,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia_orcamento = nr_sequencia_orcamento_p;

commit;
if (ie_consiste_regra_plano_w = 'S' or
    ie_consiste_regra_plano_w = 'M') then
	CALL Atualizar_Cobertura_Orcamento(nr_sequencia_orcamento_p,
								  nm_usuario_p);
end if;

end;
$body$
