CREATE OR REPLACE PROCEDURE alterar_estagio_pend_prot_doc ( nr_seq_protocolo_p bigint, nr_seq_tipo_p bigint, cd_estagio_origem_p bigint, cd_estagio_destino_p bigint, ds_historio_p text, nm_usuario_p text) AS $body$
DECLARE

c01 CURSOR FOR 
	SELECT	distinct 
		a.nr_sequencia 
	from	cta_pendencia a, 
		protocolo_doc_item b 
	where	a.nr_seq_tipo		= nr_seq_tipo_p 
	and	a.nr_seq_estagio 	= cd_estagio_origem_p 
	and	a.nr_interno_conta	= b.nr_seq_interno 
	and	b.nr_sequencia		= nr_seq_protocolo_p;
nr_seq_pend_w 	bigint;

BEGIN
	if (coalesce(nr_seq_protocolo_p,0) <> 0) and (coalesce(nr_seq_tipo_p,0) <> 0) and (coalesce(cd_estagio_origem_p,0) <> 0) and (coalesce(cd_estagio_destino_p,0) <> 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
		open c01;
		loop	 
		fetch c01 into nr_seq_pend_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
			CALL cta_alterar_estagio_pend(nr_seq_pend_w, cd_estagio_destino_p, ds_historio_p, nm_usuario_p);
			end;
		end loop;
		close c01;
	end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
