CREATE OR REPLACE PROCEDURE alterar_exame_amostra (nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text, ie_amostra_p text default null, ie_status_atend_p bigint DEFAULT NULL) AS $body$
BEGIN

if (ie_status_atend_p < 20) then
	begin
	if (ie_amostra_p = 'S')then
		begin
			update 	prescr_procedimento a
			set	a.ie_amostra 		= 'N',
				a.dt_atualizacao	= clock_timestamp(),
				a.nm_usuario		= nm_usuario_p
			where  	a.nr_prescricao 	= nr_prescricao_p
			and	a.nr_sequencia 		= nr_seq_prescr_p
			and 	a.ie_amostra 		= 'S';
		end;
	elsif (ie_amostra_p = 'N') then
		begin
			update 	prescr_procedimento a
			set	a.ie_amostra 		= 'S',
				a.dt_atualizacao	= clock_timestamp(),
				a.nm_usuario		= nm_usuario_p
			where  	a.nr_prescricao 	= nr_prescricao_p
			and	a.nr_sequencia 		= nr_seq_prescr_p
			and	a.ie_amostra		= 'N';
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
