SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_fornec_nota_fiscal ( nr_sequencia_p bigint, cd_cgc_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
nr_nota_fiscal_w		varchar(255);
cd_serie_nf_w			nota_fiscal.cd_serie_nf%type;
nr_sequencia_nf_w		bigint;
nr_Sequencia_w			bigint;
qt_registro_w			bigint;

c01 CURSOR FOR
SELECT	nr_Sequencia
from	nota_fiscal
where	cd_estabelecimento = cd_estabelecimento_w
and	cd_cgc_emitente = cd_cgc_p
and	cd_serie_nf = cd_serie_nf_w
and	nr_nota_fiscal = nr_nota_fiscal_w
and	nr_sequencia_nf = nr_sequencia_nf_w
and	ie_situacao = '1';


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	cd_estabelecimento,
		nr_nota_fiscal,
		cd_serie_nf,
		nr_sequencia_nf
	into STRICT	cd_estabelecimento_w,
		nr_nota_fiscal_w,
		cd_serie_nf_w,
		nr_sequencia_nf_w
	from	nota_fiscal
	where	nr_sequencia = nr_sequencia_p;

	select	count(*)
	into STRICT	qt_registro_w
	from	nota_fiscal
	where	cd_estabelecimento = cd_estabelecimento_w
	and	cd_cgc_emitente = cd_cgc_p
	and	cd_serie_nf = cd_serie_nf_w
	and	nr_nota_fiscal = nr_nota_fiscal_w
	and	nr_sequencia_nf = nr_sequencia_nf_w
	and	ie_situacao = '1';

	if (qt_registro_w > 0) then

		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			nr_sequencia_w := nr_sequencia_w;
			end;
		end loop;
		close C01;

		if (nr_sequencia_w > 0) then
			CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(237506,'NR_SEQUENCIA_W='||nr_sequencia_w);
		end if;

	else

		select	count(*)
		into STRICT	qt_registro_w
		from	nota_fiscal
		where	cd_estabelecimento = cd_estabelecimento_w
		and	cd_cgc_emitente = cd_cgc_p
		and	cd_serie_nf = cd_serie_nf_w
		and	nr_nota_fiscal = nr_nota_fiscal_w
		and	nr_sequencia_nf = nr_sequencia_nf_w
		and	ie_situacao <> '1';

		if (qt_registro_w > 0) then

			select	coalesce(max(nr_sequencia_nf),0) + 1
			into STRICT	nr_sequencia_nf_w
			from	nota_fiscal
			where	cd_estabelecimento = cd_estabelecimento_w
			and	cd_cgc_emitente = cd_cgc_p
			and	cd_serie_nf = cd_serie_nf_w
			and	nr_nota_fiscal = nr_nota_fiscal_w;

			update	nota_fiscal
			set	nr_sequencia_nf = nr_sequencia_nf_w,
				cd_cgc_emitente = cd_cgc_p,
				cd_cgc = cd_cgc_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_sequencia_p;
		else

			update	nota_fiscal
			set	cd_cgc_emitente = cd_cgc_p,
				cd_cgc = cd_cgc_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_sequencia_p;

		end if;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
