CREATE OR REPLACE PROCEDURE alterar_horario_disp ( nr_seq_disp_pac_p bigint, dt_ret_prevista_p timestamp, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


dt_retirada_prev_w	timestamp;


BEGIN

if (nr_seq_disp_pac_p > 0) and (dt_ret_prevista_p IS NOT NULL AND dt_ret_prevista_p::text <> '') then

	select	dt_retirada_prev
	into STRICT	dt_retirada_prev_w
	from	atend_pac_dispositivo
	where	nr_sequencia		= nr_seq_disp_pac_p;

	update	atend_pac_dispositivo
	set	dt_retirada_prev	= dt_ret_prevista_p,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_disp_pac_p;

	insert into log_dispositivo(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_disp_pac,
		ds_justificativa,
		dt_vencimento_anterior)
	values (
		nextval('log_dispositivo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_disp_pac_p,
		substr(ds_justificativa_p,1,255),
		dt_retirada_prev_w);

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
