SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_hor_agenda_exame ( cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p text, nr_sequencia_p bigint) AS $body$
DECLARE


ie_perm_alt_horario_gerado_w	varchar(1);
qt_horarios_gerados_w		bigint;
hr_inicio_w			timestamp;
ds_retorno_w			varchar(255);
nr_minuto_duracao_w		bigint;
hr_inicio_alt_w			timestamp;
hr_agenda_w			timestamp;
dt_agenda_w			timestamp;


BEGIN

select	coalesce(count(*), 0)
into STRICT	qt_horarios_gerados_w
from	agenda_paciente a
where	a.cd_agenda						= cd_agenda_p
and	a.dt_agenda						= dt_agenda_p
and	to_char(a.hr_inicio, 'hh24:mi:ss')			= to_char(to_date(hr_inicio_p,'dd/mm/yyyy hh24:mi:ss'),'hh24:mi:ss')
and	a.ie_status_agenda					<> 'L'
and	a.nr_sequencia	<> nr_sequencia_p;


if (qt_horarios_gerados_w = 0)then

	select	max(a.hr_inicio),
		max(a.nr_minuto_duracao)
	into STRICT	hr_inicio_w,
		nr_minuto_duracao_w
	from	agenda_paciente a
	where	a.cd_agenda		= cd_agenda_p
	and	a.dt_agenda		= dt_agenda_p
	and	a.nr_sequencia		= nr_sequencia_p;


	hr_inicio_alt_w := to_date(to_char(dt_agenda_p,'dd/mm/yyyy')||' '||to_char(to_date(hr_inicio_p,'dd/mm/yyyy hh24:mi:ss'),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

	if (hr_inicio_alt_w between hr_inicio_w and trunc(hr_inicio_w,'hh') + 59/1440) then
		ds_retorno_w := '';
	else
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(262648);
	end if;


else
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262649);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
