CREATE OR REPLACE PROCEDURE alterar_juros_multa ( nr_titulo_p bigint, tx_juros_p bigint, tx_multa_p bigint, cd_tipo_taxa_juro_p bigint, cd_tipo_taxa_multa_p bigint, nm_usuario_p text) AS $body$
BEGIN

	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

		update	titulo_receber
		set	tx_juros = tx_juros_p,
			tx_multa = tx_multa_p,
			cd_tipo_taxa_juro = cd_tipo_taxa_juro_p,
			cd_tipo_taxa_multa = cd_tipo_taxa_multa_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_titulo = nr_titulo_p;

		commit;

	end if;

end;
$body$
LANGUAGE PLPGSQL
