CREATE OR REPLACE PROCEDURE alterar_just_analise_vv ( nr_seq_analise_p bigint, ds_just_alteracao_p text, nm_usuario_p text, ds_justificativa_p text ) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN
	select	count(mosav.nr_sequencia)
	into STRICT	qt_existe_w
	from	man_ordem_serv_analise_vv mosav
	where	mosav.nr_sequencia = nr_seq_analise_p;
	
	if (	qt_existe_w = 1
		and	(ds_just_alteracao_p IS NOT NULL AND ds_just_alteracao_p::text <> '')) then
		begin
			update	man_ordem_serv_analise_vv
			set	ds_justificativa_alteracao	= ds_just_alteracao_p,
				dt_justificativa_alteracao	= clock_timestamp(),
				nm_usuario			= nm_usuario_p,
				dt_atualizacao			= clock_timestamp(),
				ds_justificativa		= ds_justificativa_p
			where	nr_sequencia = nr_seq_analise_p;
			
			commit;
		end;
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
