CREATE OR REPLACE PROCEDURE alterar_material_oc_nf ( nr_seq_nota_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_material_nf_w			bigint;
cd_material_oc_w			bigint;
nr_item_oci_w			bigint;
nr_ordem_compra_w		bigint;

c01 CURSOR FOR
SELECT	cd_material,
	nr_ordem_compra,
	nr_item_oci
from	nota_fiscal_item
where	nr_sequencia = nr_seq_nota_p
and	(nr_ordem_compra IS NOT NULL AND nr_ordem_compra::text <> '')
and	(nr_item_oci IS NOT NULL AND nr_item_oci::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	cd_material_nf_w,
	nr_ordem_compra_w,
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(cd_material),0)
	into STRICT	cd_material_oc_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_w
	and	nr_item_oci = nr_item_oci_w;

	if (cd_material_oc_w > 0) and (cd_material_oc_w <> cd_material_nf_w) then

		update	ordem_compra_item
		set	cd_material = cd_material_nf_w
		where	nr_ordem_compra = nr_ordem_compra_w
		and	nr_item_oci = nr_item_oci_w;

		CALL inserir_historico_ordem_compra(
			nr_ordem_compra_w,
			'S',
			WHEB_MENSAGEM_PCK.get_texto(297072),
			WHEB_MENSAGEM_PCK.get_texto(297073,'NR_ITEM_OCI_W=' || nr_item_oci_w || ';' || 'CD_MATERIAL_OC_W=' || cd_material_oc_w || ';' ||
							'CD_MATERIAL_NF_W=' || cd_material_nf_w),
			nm_usuario_p);
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
