CREATE OR REPLACE PROCEDURE alterar_material_protocolo ( cd_material_origem_p bigint, cd_material_destino_p bigint, cd_protocolo_p bigint default 0, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


ie_erro_w	     varchar(1) := 'N';
cd_estabelecimento_w smallint;


BEGIN

select wheb_usuario_pck.get_cd_estabelecimento
       into STRICT cd_estabelecimento_w
;

if (cd_material_origem_p IS NOT NULL AND cd_material_origem_p::text <> '') and (cd_material_destino_p IS NOT NULL AND cd_material_destino_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	CASE WHEN coalesce(count(*),0)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_erro_w
	from	material
	where	cd_material = cd_material_destino_p;

	if (ie_erro_w = 'N') then
		begin
		if (cd_protocolo_p > 0) then
			update	protocolo_medic_material
			set	cd_material	= cd_material_destino_p,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	cd_material	= cd_material_origem_p
			And cd_protocolo in (	SELECT cd_protocolo
						from 	protocolo
									where (cd_estabelecimento = cd_estabelecimento_w
						or	coalesce(cd_estabelecimento::text, '') = '')
						and cd_protocolo = cd_protocolo_p);
		else
			update	protocolo_medic_material
			set	cd_material	= cd_material_destino_p,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	cd_material	= cd_material_origem_p
			and cd_protocolo in (	SELECT cd_protocolo
						from 	protocolo
									where 	cd_estabelecimento = cd_estabelecimento_w
						or	coalesce(cd_estabelecimento::text, '') = '');
		end if;
		end;
	end if;

	if (ie_erro_w = 'S') then
		begin
		CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(221446);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
