SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_medico_executor_agenda (cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_medico_exec_w		varchar(10);
dt_agenda_w			timestamp;
hr_inicial_w			timestamp		:= null;
hr_final_w			timestamp		:= null;
nr_minuto_duracao_w		bigint;
cd_medico_anterior_W		varchar(10)	:= '0';
nr_seq_horario_w		bigint;
nr_seq_agenda_w			    agenda_paciente.nr_sequencia%type;
nr_seq_medico_exec_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		hr_inicio,
		cd_medico_exec,
		nr_minuto_duracao
	from	agenda_paciente
	where	cd_agenda	= cd_agenda_p
	and	dt_agenda	= dt_agenda_p
	order by hr_inicio;


BEGIN

delete 	from agenda_horario_esp
where	cd_agenda	= cd_agenda_p
and	dt_agenda	= dt_agenda_p;

open	c01;
loop
fetch	c01 into
	nr_seq_agenda_w,
	dt_agenda_w,
	cd_medico_exec_w,
	nr_minuto_duracao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin	

	if (cd_medico_anterior_w = '0') then
		cd_medico_anterior_w := cd_medico_exec_w;
	end if;

	if (coalesce(hr_inicial_w::text, '') = '') then

		if (hr_final_w IS NOT NULL AND hr_final_w::text <> '') then
 			hr_inicial_w	:= dt_agenda_w - nr_minuto_duracao_w / 1440;	
		else
 			hr_inicial_w	:= dt_agenda_w;	
		end if;
	end if;

	hr_final_w		:= dt_agenda_w;

	if (cd_medico_anterior_w <> cd_medico_exec_w) then
		begin

		select	max(nr_sequencia)
		into STRICT	nr_seq_medico_exec_w
		from	agenda_medico
		where	cd_agenda	= cd_agenda_p
		and	cd_medico	= cd_medico_anterior_w;


		select	nextval('agenda_horario_esp_seq')
		into STRICT	nr_seq_horario_w
		;		

		insert	into agenda_horario_esp(nr_sequencia,
			cd_agenda,
			dt_agenda,
			dt_atualizacao,
			nm_usuario,
			hr_inicial,
			hr_final,
			nr_minuto_intervalo,
			nr_seq_medico_exec,
			IE_HORARIO_ADICIONAL)
		values (nr_seq_horario_w,
			cd_agenda_p,
			dt_agenda_p,
			clock_timestamp(),
			nm_usuario_p,
			hr_inicial_w,
			hr_final_w,
			nr_minuto_duracao_w,
			nr_seq_medico_exec_w, 'N');

		hr_inicial_w := null;

		cd_medico_anterior_w := cd_medico_exec_w;

		end;
	end if;	

	end;
end loop;
close c01;

if (cd_medico_exec_w IS NOT NULL AND cd_medico_exec_w::text <> '') then
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_medico_exec_w
	from	agenda_medico
	where	cd_agenda	= cd_agenda_p
	and	cd_medico	= cd_medico_exec_w;


	select	nextval('agenda_horario_esp_seq')
	into STRICT	nr_seq_horario_w
	;		

	insert	into agenda_horario_esp(nr_sequencia,
		cd_agenda,
		dt_agenda,
		dt_atualizacao,
		nm_usuario,
		hr_inicial,
		hr_final,
		nr_minuto_intervalo,
		nr_seq_medico_exec,
		IE_HORARIO_ADICIONAL)
	values (nr_seq_horario_w,
		cd_agenda_p,
		dt_agenda_p,
		clock_timestamp(),
		nm_usuario_p,
		hr_inicial_w,
		hr_final_w + nr_minuto_duracao_w / 1440,
		nr_minuto_duracao_w,
		nr_seq_medico_exec_w, 'N');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
