CREATE OR REPLACE PROCEDURE alterar_medico_executor_proc ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_medico_exec_p text, nm_usuario_p text) AS $body$
DECLARE


ie_altera_proc_paciente_w	varchar(1);
ie_conta_definitiva_w		varchar(1) := 'N';

BEGIN

ie_altera_proc_paciente_w := obter_valor_param_usuario(916,826,obter_perfil_ativo,nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento);

if (ie_altera_proc_paciente_w <> 'P') then

	if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then

		update	prescr_procedimento
		set		cd_medico_exec 		= cd_medico_exec_p,
				dt_atualizacao 		= clock_timestamp(),
				nm_usuario 	   		= nm_usuario_p
		where	nr_prescricao  		= nr_prescricao_p
		and		nr_sequencia   		= nr_sequencia_p;

		if (coalesce(ie_altera_proc_paciente_w,'N') = 'S') then

			update	procedimento_paciente
			set		cd_medico_executor 	= cd_medico_exec_p,
					nm_usuario         	= nm_usuario_p,
					dt_atualizacao     	= clock_timestamp()
			where	nr_prescricao      	= nr_prescricao_p
			and	nr_sequencia_prescricao = nr_sequencia_p;
		end if;
	end if;

elsif (ie_altera_proc_paciente_w = 'P') then

	if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then

		begin
			select	'S'
			into STRICT	ie_conta_definitiva_w
			from	conta_paciente a,
					procedimento_paciente b
			where	a.nr_interno_conta	= b.nr_interno_conta
			and		a.ie_status_acerto	= 2
			and		nr_prescricao      		= nr_prescricao_p
			and		nr_sequencia_prescricao	= nr_sequencia_p  LIMIT 1;
		exception
		when others then
			ie_conta_definitiva_w 	:= 'N';
		end;

		if (ie_conta_definitiva_w = 'S') then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(1046560);
		end if;

		update	prescr_procedimento
		set		cd_medico_exec 		= cd_medico_exec_p,
				dt_atualizacao 		= clock_timestamp(),
				nm_usuario 			= nm_usuario_p
		where	nr_prescricao 		= nr_prescricao_p
		and		nr_sequencia 		= nr_sequencia_p;

		update	procedimento_paciente
		set		cd_medico_executor 		= cd_medico_exec_p,
				nm_usuario         		= nm_usuario_p,
				dt_atualizacao     		= clock_timestamp()
		where	nr_prescricao      		= nr_prescricao_p
		and		nr_sequencia_prescricao	= nr_sequencia_p;

	end if;

end if;

commit;

end;
$body$
