CREATE OR REPLACE PROCEDURE alterar_modulo_requisito (nr_sequencia_p text ,nr_seq_modulo_p latam_requisito.nr_seq_modulo%type ) AS $body$
DECLARE

    ds_cmd_update_w   varchar(4000);

  i RECORD;

BEGIN
ds_cmd_update_w := 'update latam_requisito set nr_seq_modulo = :1 '
                || ' where nr_sequencia = :2';

FOR i IN (WITH RECURSIVE cte AS (
SELECT regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS nr_sequencia

         (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')  UNION ALL
SELECT regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS nr_sequencia
            
         (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '') JOIN cte c ON ()

) SELECT * FROM cte;
)
LOOP
    EXECUTE ds_cmd_update_w USING nr_seq_modulo_p, i.nr_sequencia;
END LOOP;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
