CREATE OR REPLACE PROCEDURE alterar_motivo_cancel_agenda ( cd_motivo_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
ie_gera_historico_w		varchar(1);


BEGIN 
 
ie_gera_historico_w := obter_param_usuario(871, 714, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_gera_historico_w);
 
if	(cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '' AND cd_motivo_p <> '0') then 
 
 
	update agenda_paciente 
	set	dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p, 
		cd_motivo_cancelamento	= cd_motivo_p 
	where	nr_sequencia		= nr_sequencia_p;
	 
	 
	commit;
	 
	if (ie_gera_historico_w = 'S') then 
		CALL gerar_agenda_cancel_historico(nr_sequencia_p, cd_motivo_p, nm_usuario_p,'A');
	end if;
end if;
 
end;
$body$
