SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_natureza_item_nf ( nr_item_nf_p bigint, nr_sequencia_p bigint, cd_natureza_operacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_natureza_operacao_w 	bigint;
cd_material_w		integer;
ds_material_w		varchar(255);
historico_w		varchar(2000);


BEGIN

if (nr_item_nf_p IS NOT NULL AND nr_item_nf_p::text <> '') then

	select 	cd_natureza_operacao,
		cd_material,
		substr(obter_desc_material(cd_material),1,255) ds_material
	into STRICT 	cd_natureza_operacao_w,
		cd_material_w,
		ds_material_w
	from nota_fiscal_item
	where 	nr_sequencia = nr_sequencia_p
		and nr_item_nf = nr_item_nf_p;

	update 	nota_fiscal_item
	set 	cd_natureza_operacao = cd_natureza_operacao_p
	where 	nr_sequencia = nr_sequencia_p
		and nr_item_nf = nr_item_nf_p;

	historico_w := 	substr(wheb_mensagem_pck.get_texto(315074) || ' [' || nr_item_nf_p || '] ' || cd_material_w || ' (' || ds_material_w || '). ' ||
			wheb_mensagem_pck.get_texto(310560)|| ' ' ||  substr(cd_natureza_operacao_w,1,15) || chr(13) || chr(10) ||
			wheb_mensagem_pck.get_texto(280393)||' ' ||  substr(cd_natureza_operacao_p,1,15),1,2000);

	CALL gerar_historico_nota_fiscal(
		nr_sequencia_p,
		nm_usuario_p,
		34,
		historico_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
