SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_news_idioma ( nr_sequencia_p bigint, ds_alteracao_p text, ds_objetivo_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT  b.nr_sequencia,
	b.ds_locale,
	b.nr_seq_release,
	(	select	max(d.cd_exp_campo)
		from	release_news_template_inf d
		where	d.nr_seq_template = a.nr_seq_template_alt) nr_seq_template_alt,
	(	select	max(d.cd_exp_campo)
		from	release_news_template_inf d
		where	d.nr_seq_template = a.nr_seq_template_obj) nr_seq_template_obj
from    release_news_idioma b,
	release_news a
where   b.nr_seq_release =  nr_sequencia_p
and	b.nr_seq_release = a.nr_sequencia;

BEGIN
	for r_c01 in c01
		loop

			update	release_news_idioma
			set	ds_objetivo  = expressao_pck.obter_dic_expressao_loc(r_c01.nr_seq_template_obj, r_c01.ds_locale) || ' ' || ds_objetivo_p,
				ds_alteracao  = expressao_pck.obter_dic_expressao_loc(r_c01.nr_seq_template_alt, r_c01.ds_locale) || ' ' || ds_alteracao_p
			where	nr_sequencia = r_c01.nr_sequencia;

		end loop;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
