CREATE OR REPLACE PROCEDURE alterar_obs_adiant_html5 ( ds_observacao_p text, nm_usuario_p text, nr_adiantamento_p bigint) AS $body$
DECLARE



ds_observacao_w		adiantamento.ds_observacao%type;



BEGIN

select	max(ds_observacao)
into STRICT	ds_observacao_w
from	adiantamento
where	nr_adiantamento	= nr_adiantamento_p;

update	adiantamento
set	ds_observacao	= ds_observacao_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_adiantamento	= nr_adiantamento_p;

commit;


CALL GERAR_ADIANTAMENTO_HIST(nr_adiantamento_p,
	'A',
	clock_timestamp(),
	wheb_mensagem_pck.GET_TEXTO(210393, 'OBSERVACAO_ANT=' || ds_observacao_w || ';OBSERVACAO_ATUAL=' || ds_observacao_p),
	nm_usuario_p,
	'N');

commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
