CREATE OR REPLACE PROCEDURE alterar_obs_alta_eup (nr_atendimento_p INOUT bigint, cd_motivo_alta_p INOUT bigint, dt_alta_p INOUT timestamp, nm_usuario_alta_p INOUT text, dt_atualizacao_p INOUT timestamp, ds_obs_alta_p INOUT text, ie_necropsia_p INOUT text, ie_tipo_atendimento_p INOUT text, ie_obito_p INOUT text, cd_setor_obito_p INOUT bigint, nr_seq_motivo_p INOUT bigint, cd_especialidade_p INOUT bigint, cd_cgc_p INOUT bigint, nm_usuario_p INOUT text) AS $body$
BEGIN

select	cd_motivo_alta,
		dt_alta,
		nr_atendimento,
		nm_usuario_alta,
		nm_usuario,
		dt_atualizacao,
		ds_obs_alta,
		ie_necropsia,
		ie_tipo_atendimento
into STRICT	cd_motivo_alta_p,
		dt_alta_p,
		nr_atendimento_p,
		nm_usuario_alta_p,
		nm_usuario_p,
		dt_atualizacao_p,
		ds_obs_alta_p,
		ie_necropsia_p,
		ie_tipo_atendimento_p
from    atendimento_paciente
where   nr_atendimento = nr_atendimento_p;

ie_obito_p := obter_se_alta_obito(nr_atendimento_p);

select 	max(cd_setor_obito)
into STRICT	cd_setor_obito_p
from 	atendimento_paciente
where 	nr_atendimento = nr_atendimento_p;

select	coalesce(max(nr_seq_motivo),0) nr_seq_motivo,
		coalesce(max(cd_especialidade),0) cd_especialidade,
		max(cd_cgc) cd_cgc
into STRICT	nr_seq_motivo_p,
		cd_especialidade_p,
		cd_cgc_p
from	atendimento_transf
where	nr_atendimento = nr_atendimento_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
