SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_obs_canc_titulo_pagar ( ds_observacao_p text, nr_titulo_p bigint) AS $body$
DECLARE


ds_observacao_w			varchar(4000);
ds_observacao_titulo_w	varchar(4000);


BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin
	ds_observacao_w := substr(ds_observacao_p,1,4000);

	select	ds_observacao_titulo
	into STRICT	ds_observacao_titulo_w
	from	titulo_pagar
	where	nr_titulo = nr_titulo_p;

	ds_observacao_titulo_w := substr(ds_observacao_titulo_w||ds_observacao_w,1,4000);

	update   	titulo_pagar
	set      	ds_observacao_titulo = substr(ds_observacao_titulo_w,1,4000)
	where    	nr_titulo = nr_titulo_p;
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
