CREATE OR REPLACE PROCEDURE alterar_obs_item_req_mat ( cd_material_p bigint, ds_observacao_p text, nr_requisicao_p bigint) AS $body$
DECLARE


nr_sequencia_v			bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_v
from	item_requisicao_material
where	nr_requisicao	= nr_requisicao_p
and	cd_material	= cd_material_p;

update	item_requisicao_material
set	ds_observacao = ds_observacao_p
where	nr_requisicao  = nr_requisicao_p
and	nr_sequencia   = nr_sequencia_v;

end;
$body$
LANGUAGE PLPGSQL
