CREATE OR REPLACE PROCEDURE alterar_obs_solic ( nr_solic_compra_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_observacao_w				solic_compra.ds_observacao%type;
ds_historico_w				varchar(4000);
				

BEGIN 
			 
select	coalesce(ds_observacao,WHEB_MENSAGEM_PCK.get_texto(297053)) 
into STRICT	ds_observacao_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
update	solic_compra 
set	ds_observacao 	= substr(ds_observacao_p,1,2000) 
where	nr_solic_compra	= nr_solic_compra_p;
			 
ds_historico_w	:=	substr(WHEB_MENSAGEM_PCK.get_texto(297058,'DS_OBSERVACAO_W=' || ds_observacao_w || ';' || 'DS_OBSERVACAO_P=' || ds_observacao_p),1,4000);
 
	CALL gerar_historico_solic_compra( 
		nr_solic_compra_p, 
		WHEB_MENSAGEM_PCK.get_texto(297057), 
		ds_historico_w, 
		'AO', 
		nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
