CREATE OR REPLACE PROCEDURE alterar_operacao_prontcontrole ( nr_prontuario_p bigint, dt_operacao_p timestamp, ie_tipo_operacao_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_prontuario_p IS NOT NULL AND nr_prontuario_p::text <> '') and (dt_operacao_p IS NOT NULL AND dt_operacao_p::text <> '') and (ie_tipo_operacao_p IS NOT NULL AND ie_tipo_operacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update	prontuario_controle
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_tipo_operacao		= 'E',
		nm_usuario_emprestimo   	= nm_usuario_p
	where	nr_prontuario 		= nr_prontuario_p
	and	dt_operacao		= dt_operacao_p
	and	ie_tipo_operacao		= ie_tipo_operacao_p;
	end;
end if;
commit;
end;
