CREATE OR REPLACE PROCEDURE alterar_pac_adiantamento (nr_adiantamento_p adiantamento.nr_adiantamento%type, cd_pessoa_adiant_p pessoa_fisica.cd_pessoa_fisica%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_pessoa_ant_w		adiantamento.cd_pessoa_adiant%type;


BEGIN

select	cd_pessoa_adiant
into STRICT	cd_pessoa_ant_w
from	adiantamento
where	nr_adiantamento	= nr_adiantamento_p;

update	adiantamento
set	cd_pessoa_adiant	= cd_pessoa_adiant_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_adiantamento	= nr_adiantamento_p;

CALL gerar_adiantamento_hist(nr_adiantamento_p, 'A', clock_timestamp(), wheb_mensagem_pck.get_texto(800273,
									'PACIENTE_ANT='||cd_pessoa_ant_w||
									';PACIENTE_ATUAL='||cd_pessoa_adiant_p), nm_usuario_p, 'S');
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
