CREATE OR REPLACE PROCEDURE alterar_pendencias_amostras (nr_prescricao_p bigint, ds_lista_seq_prescr_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exame_w			bigint;
ie_indetif_w			varchar(1);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		'S' ie_indetif
	from 	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	obter_se_contido(nr_sequencia,ds_lista_seq_prescr_p) = 'S'
	
union all

	SELECT	nr_sequencia,
		'N' ie_indetif
	from 	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	obter_se_contido(nr_sequencia,ds_lista_seq_prescr_p) = 'N';


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_exame_w,
	ie_indetif_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin
	if (coalesce(nr_prescricao_p, 0) > 0 ) and (coalesce(nr_seq_exame_w, 0) > 0) then

		update	prescr_procedimento
		set     ie_pendente_amostra     = ie_indetif_w,
			dt_atualizacao          = clock_timestamp(),
			nm_usuario              = nm_usuario_p
		where   nr_prescricao           = nr_prescricao_p
		and     nr_sequencia            = nr_seq_exame_w;

end if;
end;
end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
