CREATE OR REPLACE PROCEDURE alterar_peso_ciclo ( cd_pessoa_fisica_p integer, qt_peso_p text, dt_real_p timestamp, nm_usuario_p text) AS $body$
DECLARE

C01 CURSOR FOR 
	SELECT	a.nr_seq_atendimento 
	from	paciente_atendimento a, 
		paciente_Setor b 
	where 	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and 	a.nr_seq_paciente = b.nr_seq_paciente 
	and 	coalesce(a.nr_prescricao::text, '') = '' 
	and 	PKG_DATE_UTILS.start_of(a.dt_prevista, 'dd', 0) > PKG_DATE_UTILS.start_of(dt_real_p, 'dd', 0);

nr_seq_atendimento_w	varchar(10);	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
		update	paciente_atendimento 
		set	qt_peso = qt_peso_p 
		where 	nr_seq_atendimento = nr_seq_atendimento_w;
		 
		CALL atualizar_dose_onc_ciclo(nr_seq_atendimento_w);
	end if;
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
