CREATE OR REPLACE PROCEDURE alterar_pessoa_agenda (nr_seq_agenda_p bigint, cd_pessoa_fisica_P text, nm_usuario_p text) AS $body$
DECLARE


dt_nascimento_w		timestamp;
qt_idade_w			bigint;
cd_pessoa_agenda_w		varchar(10);


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_agenda_w
from	agenda_paciente
where	nr_sequencia		= nr_seq_agenda_p;

select	obter_idade(dt_nascimento, clock_timestamp(), 'A'),
	dt_nascimento
into STRICT	qt_idade_w,
	dt_nascimento_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

update	agenda_paciente
set	cd_pessoa_fisica	= cd_pessoa_fisica_p,
	dt_nascimento_pac	= dt_nascimento_w,
	qt_idade_paciente	= qt_idade_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_agenda_p;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
