CREATE OR REPLACE PROCEDURE alterar_pessoa_caixa_receb ( cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_receb_p bigint) AS $body$
BEGIN
if (nr_seq_receb_p IS NOT NULL AND nr_seq_receb_p::text <> '') then
	begin
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
