CREATE OR REPLACE PROCEDURE alterar_pessoa_cirurgia ( nr_seq_agenda_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


cd_pessoa_agenda_w	varchar(10);
nr_cirurgia_w		bigint;
nr_atendimento_w	bigint;


BEGIN

select	max(cd_pessoa_fisica),
	max(nr_cirurgia)
into STRICT	cd_pessoa_agenda_w,
	nr_cirurgia_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	cirurgia
where	nr_cirurgia = nr_cirurgia_w;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266283);
end if;

if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	update	prescr_medica
	set	cd_pessoa_fisica	= cd_pessoa_fisica_p
	where	nr_seq_agenda		= nr_seq_agenda_p
	and	coalesce(nr_atendimento::text, '') = '';

	update	cirurgia
	set	cd_pessoa_fisica	= cd_pessoa_fisica_p
	where	nr_cirurgia		= nr_cirurgia_w
	and	coalesce(nr_atendimento::text, '') = '';

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
