CREATE OR REPLACE PROCEDURE alterar_pf_pj_titulo_receber ( nr_titulo_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	insert into tit_rec_alt_pessoa(
		nr_sequencia,
		nr_titulo,
		cd_cgc,
		cd_cgc_ant,
		cd_pessoa_fisica,
		cd_pessoa_fisica_ant,
		dt_alteracao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec)
	SELECT	nextval('tit_rec_alt_pessoa_seq'),
		a.nr_titulo,
		cd_cgc_p,
		a.cd_cgc,
		cd_pessoa_fisica_p,
		a.cd_pessoa_fisica,
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p
	from	titulo_receber a
	where	nr_titulo = nr_titulo_p;

	update	titulo_receber
	set	cd_pessoa_fisica	= cd_pessoa_fisica_p,
		cd_cgc			= cd_cgc_p
	where	nr_titulo		= nr_titulo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
