CREATE OR REPLACE PROCEDURE alterar_portador_titulo ( nr_titulo_p bigint, nr_seq_cobranca_p bigint, nr_seq_conta_banco_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

	 
cd_agencia_bancaria_w			banco_estabelecimento.cd_agencia_bancaria%type;
cd_banco_w				banco_estabelecimento.cd_banco%type;
ie_digito_conta_w			banco_estabelecimento.ie_digito_conta%type;
cd_conta_w				banco_estabelecimento.cd_conta%type;
	

BEGIN 
select	max(a.cd_agencia_bancaria), 
	max(a.cd_banco), 
	max(a.ie_digito_conta), 
	max(a.cd_conta) 
into STRICT	cd_agencia_bancaria_w, 
	cd_banco_w, 
	ie_digito_conta_w, 
	cd_conta_w 
from	banco_estabelecimento	a 
where	a.nr_sequencia	= nr_seq_conta_banco_p;
 
update	titulo_receber_cobr 
set	cd_agencia_bancaria	= cd_agencia_bancaria_w, 
	cd_banco		= cd_banco_w, 
	ie_digito_conta		= ie_digito_conta_w, 
	nr_conta		= cd_conta_w 
where	nr_titulo		= nr_titulo_p 
and	nr_seq_cobranca		= nr_seq_cobranca_p;
 
update	titulo_receber 
set	cd_agencia_bancaria	= cd_agencia_bancaria_w, 
	cd_banco		= cd_banco_w, 
	nr_seq_conta_banco	= nr_seq_conta_banco_p 
where	nr_titulo		= nr_titulo_p;
 
CALL Gerar_Bloqueto_Tit_Rec(	nr_titulo_p, 
				'MTR');
 
CALL inserir_hist_titulo_receb_hist(	nr_titulo_p, 
				Wheb_mensagem_pck.get_texto(303533), 
				nm_usuario_p);
 
end;
$body$
