CREATE OR REPLACE PROCEDURE alterar_prioridade_int_reg ( cd_cnpj_p text, nr_seq_regulacao_p bigint, nr_seq_pls_requisicao_p bigint default null, nr_seq_prioridade_p bigint DEFAULT NULL, ie_integracao_p text default 'N', cd_procedimento_p bigint default null) AS $body$
DECLARE



nr_seq_pls_requisicao_w 			bigint;
nr_seq_regulacao_w					varchar(10);
ie_integracao_w						varchar(1);


BEGIN

if ((nr_seq_pls_requisicao_p IS NOT NULL AND nr_seq_pls_requisicao_p::text <> '') and ie_integracao_p = 'S') then

	update regulacao_atend
	set nr_seq_prioridade = nr_seq_prioridade_p
	where nr_sequencia = nr_seq_regulacao_p;

	commit;


else if ((nr_seq_regulacao_p IS NOT NULL AND nr_seq_regulacao_p::text <> '') and ie_integracao_p = 'S' and coalesce(nr_seq_pls_requisicao_p::text, '') = '') then

		select max(nr_sequencia)
		into STRICT nr_seq_pls_requisicao_w
		from pls_requisicao
		where nr_seq_regulacao = nr_seq_regulacao_p;

		update pls_requisicao
		set nr_seq_prioridade = nr_seq_prioridade_p
		where nr_sequencia = nr_seq_pls_requisicao_w;

		commit;

		update pls_requisicao_proc
		set  nr_seq_prioridade = nr_seq_prioridade_p
		where cd_procedimento = cd_procedimento_p
		and nr_seq_requisicao = nr_seq_pls_requisicao_p;

		commit;

	end if;

end if;

END;
$body$
