CREATE OR REPLACE PROCEDURE alterar_prioridade_reg_pls ( nr_sequencia_p bigint, nr_seq_prioridade_p bigint, ds_justificativa_p text, nm_usuario_p text, cd_procedimento_p bigint) AS $body$
DECLARE



nr_seq_pls_requisicao_w 				bigint;
nr_seq_regulacao_w					varchar(10);
ie_integracao_w						varchar(1);


BEGIN

if (nr_seq_prioridade_p IS NOT NULL AND nr_seq_prioridade_p::text <> '') then

	select max(nr_seq_requisicao)
	into STRICT nr_seq_pls_requisicao_w
	from pls_auditoria
	where nr_sequencia = nr_sequencia_p;

	if (nr_seq_pls_requisicao_w IS NOT NULL AND nr_seq_pls_requisicao_w::text <> '') then

		select max(nr_seq_regulacao)
		into STRICT nr_seq_regulacao_w
		from pls_requisicao
		where nr_sequencia = nr_seq_pls_requisicao_w;

		CALL Alterar_prioridade_regulacao(nr_seq_regulacao_w, nr_seq_prioridade_p,null,ds_justificativa_p,null,null,null,null,null,nm_usuario_p,nr_seq_pls_requisicao_w,'N',cd_procedimento_p);

	end if;

end if;



END;
$body$
