CREATE OR REPLACE PROCEDURE alterar_procedimento_sugerido ( nr_seq_regra_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_interno_w		bigint;
nr_seq_proc_sugerido_w		bigint;

cd_tipo_cirurgia_w			proc_interno.cd_tipo_cirurgia%TYPE;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select  nr_seq_proc_interno,
		nr_seq_proc_sugerido
	into STRICT	nr_seq_proc_interno_w,
		nr_seq_proc_sugerido_w
	from    pepo_proced_confirmacao
	where   nr_sequencia = nr_seq_regra_p;

	if (nr_seq_proc_sugerido_w IS NOT NULL AND nr_seq_proc_sugerido_w::text <> '') then

		cd_tipo_cirurgia_w := obter_dados_proc_interno(nr_seq_proc_sugerido_w,'TC');

		update  cirurgia
		set 	nr_seq_proc_interno  = nr_seq_proc_sugerido_w,
				cd_tipo_cirurgia 	 =  cd_tipo_cirurgia_w
		where   nr_cirurgia = nr_cirurgia_p;
		commit;

		CALL alterar_procedimento_agenda(nr_cirurgia_p);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
