CREATE OR REPLACE PROCEDURE alterar_proced_atend_ciclo ( nr_seq_atendimento_p bigint, nr_seq_procedimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estab_usuario_w		smallint;
nr_seq_atendimento_w		bigint;
nr_seq_paciente_w		bigint;
nr_seq_procedimento_w		integer;
cd_procedimento_w		bigint;
nr_agrupamento_w		integer;

c01 CURSOR FOR
	SELECT	*
	from	paciente_atend_proc
	where	nr_seq_atendimento	= nr_seq_atendimento_p
	and	nr_seq_procedimento	= nr_seq_procedimento_p;
c01_w	c01%rowtype;

C02 CURSOR FOR
	SELECT	a.nr_seq_atendimento,
		b.nr_seq_procedimento
	from	paciente_atendimento a,
		paciente_atend_proc b
	where	a.nr_seq_atendimento 	= b.nr_seq_atendimento
	and	a.nr_seq_paciente	= nr_seq_paciente_w
	and	b.cd_procedimento 	= cd_procedimento_w
	and	b.nr_agrupamento 	= nr_agrupamento_w
	and	coalesce(nr_prescricao::text, '') = '';


BEGIN

select	nr_seq_paciente
into STRICT	nr_seq_paciente_w
from	paciente_atendimento
where	nr_seq_atendimento	= nr_seq_atendimento_p;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_procedimento_w	:= c01_w.cd_procedimento;
	nr_agrupamento_w	:= c01_w.nr_agrupamento;


	open C02;
	loop
	fetch C02 into
		nr_seq_atendimento_w,
		nr_seq_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	paciente_atend_proc
		set	cd_procedimento 		= c01_w.cd_procedimento,
			ie_origem_proced 		= c01_w.ie_origem_proced,
			qt_procedimento 		= c01_w.qt_procedimento,
			dt_atualizacao 			= clock_timestamp(),
			nm_usuario 			= nm_usuario_p,
			cd_intervalo 			= c01_w.cd_intervalo,
			nr_agrupamento 			= c01_w.nr_agrupamento,
			ie_se_necessario 		= c01_w.ie_se_necessario,
			ie_acm 				= c01_w.ie_acm,
			nr_seq_proc_interno 		= c01_w.nr_seq_proc_interno,
			ie_lado 			= c01_w.ie_lado,
			nr_seq_ordem_adep 		= c01_w.nr_seq_ordem_adep
		where	nr_seq_atendimento 		= nr_seq_atendimento_w
		and	nr_seq_procedimento 		= nr_seq_procedimento_w;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
