CREATE OR REPLACE PROCEDURE alterar_proj_rec_cot_compra ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, nr_seq_proj_rec_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_item_cot_compra_w			integer;
ds_prof_rec_antigo_w			varchar(80);
ds_prof_rec_novo_w			varchar(80);
cd_material_w				integer;
nr_seq_proj_rec_w				bigint;
ds_historico_w				varchar(4000);

c01 CURSOR FOR
	SELECT	distinct
		nr_item_cot_compra
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_p
	and	((ie_opcao_p = 0) or
		(ie_opcao_p = 1 AND nr_item_cot_compra = nr_item_cot_compra_p));


BEGIN

open C01;
loop
fetch C01 into
	nr_item_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_seq_proj_rec),0),
		max(cd_material)
	into STRICT	nr_seq_proj_rec_w,
		cd_material_w
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_cot_compra_w;

	select	coalesce(obter_desc_proj_recurso(nr_seq_proj_rec_w),WHEB_MENSAGEM_PCK.get_texto(297059)),
		obter_desc_proj_recurso(nr_seq_proj_rec_p)
	into STRICT	ds_prof_rec_antigo_w,
		ds_prof_rec_novo_w
	;

	update	cot_compra_item
	set	nr_seq_proj_rec = nr_seq_proj_rec_p
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_cot_compra_w;

	ds_historico_w	:=	substr(	WHEB_MENSAGEM_PCK.get_texto(297062,'CD_MATERIAL_W=' || cd_material_w || ';' || 'DS_MATERIAL_W=' || obter_desc_material(cd_material_w)
								|| ';' || 'DS_PROF_REC_ANTIGO_W=' || ds_prof_rec_antigo_w || ';' || 'DS_PROF_REC_NOVO_W=' || ds_prof_rec_novo_w),1,4000);

	CALL gerar_historico_cotacao(
		nr_cot_compra_p,
		WHEB_MENSAGEM_PCK.get_texto(297066),
		ds_historico_w,
		'S',
		nm_usuario_p);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
