CREATE OR REPLACE PROCEDURE alterar_quantidade_comp_kit ( nr_seq_kit_estoque_p bigint, nr_sequencia_p bigint, qt_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w	integer;
qt_material_w	double precision;
nr_sequencia_w	integer;
ds_historico_w	varchar(2000);
ds_titulo_w	varchar(1000);


BEGIN

select	cd_material,
	qt_material
into STRICT	cd_material_w,
	qt_material_w
from	kit_estoque_comp
where	nr_seq_kit_estoque = nr_seq_kit_estoque_p
and	nr_sequencia = nr_sequencia_p;

update	kit_estoque_comp
set	qt_material = qt_material_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_seq_kit_estoque = nr_seq_kit_estoque_p
and	nr_sequencia = nr_sequencia_p;

select	coalesce(max(nr_sequencia), 0) + 1
into STRICT	nr_sequencia_w
from	kit_estoque_comp
where	nr_seq_kit_estoque = nr_seq_kit_estoque_p;

insert into kit_estoque_comp(
	nr_seq_kit_estoque,
	nr_sequencia,
	cd_material,
	dt_atualizacao,
	nm_usuario,
	qt_material,
	ie_gerado_barras)
values (
	nr_seq_kit_estoque_p,
	nr_sequencia_w,
	cd_material_w,
	clock_timestamp(),
	nm_usuario_p,
	qt_material_w - qt_material_p,
	'N');

ds_titulo_w	:=	wheb_mensagem_pck.get_texto(315076);
ds_historico_w	:=	substr(wheb_mensagem_pck.get_texto(315077)||' ' || cd_material_w || ' - ' || obter_desc_material(cd_material_w) ||
			' '|| wheb_mensagem_pck.get_texto(310560)|| ' ' || qt_material_w ||' ' || wheb_mensagem_pck.get_texto(280393)||' ' || qt_material_p || ' '||wheb_mensagem_pck.get_texto(315075),1,1000);

CALL gravar_kit_estoque_hist(nr_seq_kit_estoque_p, ds_titulo_w, ds_historico_w, 'S', nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
