SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_quantidade_entrega ( nr_compra_p bigint, nr_seq_item_p bigint, nr_item_p bigint, qt_entrega_p bigint, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


/*Solicitacao*/

qt_entrega_solicitada_w		solic_compra_item.qt_material%type;
qt_entrega_solicitada_old_w		solic_compra_item_entrega.qt_entrega_solicitada%type;
qt_entrega_solicitada_new_w		solic_compra_item_entrega.qt_entrega_solicitada%type;
cd_material_w			material.cd_material%type;
ds_material_w			material.ds_material%type;
dt_entrega_solicitada_w		solic_compra_item_entrega.dt_entrega_solicitada%type;
/*ordem de compra*/

qt_prevista_entrega_old_w		ordem_compra_item_entrega.qt_prevista_entrega%type;
dt_prevista_entrega_w		ordem_compra_item_entrega.dt_prevista_entrega%type;
qt_prevista_entrega_new_w		ordem_compra_item_entrega.qt_prevista_entrega%type;
qt_prevista_entrega_w		ordem_compra_item_entrega.qt_prevista_entrega%type;


BEGIN

if (ie_tipo_p = 'S') then

	select	max(b.qt_entrega_solicitada)
	into STRICT	qt_entrega_solicitada_old_w
	from	solic_compra_item_entrega b,
		solic_compra_item a
	where	a.nr_solic_compra = b.nr_solic_compra
	and	a.nr_item_solic_compra = b.nr_item_solic_compra
	and	a.nr_solic_compra = nr_compra_p
	and	a.nr_item_solic_compra = nr_seq_item_p
	and	nr_item_solic_compra_entr = nr_item_p;

	update	solic_compra_item_entrega
	set	qt_entrega_solicitada = qt_entrega_p
	where	nr_solic_compra = nr_compra_p
	and	nr_item_solic_compra = nr_seq_item_p
	and	nr_item_solic_compra_entr = nr_item_p;

	select	sum(qt_entrega_solicitada)
	into STRICT	qt_entrega_solicitada_w
	from	solic_compra_item_entrega
	where	nr_solic_compra = nr_compra_p
	and	nr_item_solic_compra = nr_seq_item_p;

	update	solic_compra_item
	set	qt_material = qt_entrega_solicitada_w
	where	nr_solic_compra = nr_compra_p
	and	nr_item_solic_compra = nr_seq_item_p;

	select	max(a.cd_material),
		max(substr(obter_desc_material(a.cd_material), 1, 80)),
		max(b.dt_entrega_solicitada),
		max(b.qt_entrega_solicitada)
	into STRICT	cd_material_w,
		ds_material_w,
		dt_entrega_solicitada_w,
		qt_entrega_solicitada_new_w
	from	solic_compra_item_entrega b,
		solic_compra_item a
	where	a.nr_solic_compra = b.nr_solic_compra
	and	a.nr_item_solic_compra = b.nr_item_solic_compra
	and	a.nr_solic_compra = nr_compra_p
	and	a.nr_item_solic_compra = nr_seq_item_p
	and	nr_item_solic_compra_entr = nr_item_p;

	CALL gerar_historico_solic_compra(
			nr_compra_p,
			WHEB_MENSAGEM_PCK.get_texto(297097),
			WHEB_MENSAGEM_PCK.get_texto(	297096,
							'CD_MATERIAL_W='||cd_material_w||
							';DS_MATERIAL_W='||ds_material_w||
							';DT_ENTREGA_SOLICITADA_W='||dt_entrega_solicitada_w||
							';QT_ENTREGA_SOLICITADA_OLD_W='||qt_entrega_solicitada_old_w||
							';QT_ENTREGA_SOLICITADA_NEW_W='||qt_entrega_solicitada_new_w),
			'AQE',
			nm_usuario_p);

elsif (ie_tipo_p = 'O') then

	select	max(b.qt_prevista_entrega)
	into STRICT	qt_prevista_entrega_old_w
	from	ordem_compra_item_entrega b,
		ordem_compra_item a
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	a.nr_item_oci = b.nr_item_oci
	and	a.nr_ordem_compra = nr_compra_p
	and	a.nr_item_oci = nr_seq_item_p
	and	b.nr_sequencia = nr_item_p;

	update	ordem_compra_item_entrega
	set	qt_prevista_entrega = qt_entrega_p
	where	nr_ordem_compra = nr_compra_p
	and	nr_item_oci = nr_seq_item_p
	and	nr_sequencia = nr_item_p;

	select	sum(qt_prevista_entrega)
	into STRICT	qt_prevista_entrega_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra = nr_compra_p
	and	nr_item_oci = nr_seq_item_p;

	update	ordem_compra_item
	set	qt_material = qt_prevista_entrega_w,
		vl_total_item = (vl_unitario_material * qt_prevista_entrega_w)
	where	nr_ordem_compra = nr_compra_p
	and	nr_item_oci = nr_seq_item_p;

	select	max(a.cd_material),
		max(substr(obter_desc_material(a.cd_material), 1, 80)),
		max(b.dt_prevista_entrega),
		max(b.qt_prevista_entrega)
	into STRICT	cd_material_w,
		ds_material_w,
		dt_prevista_entrega_w,
		qt_prevista_entrega_new_w
	from	ordem_compra_item_entrega b,
		ordem_compra_item a
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	a.nr_item_oci = b.nr_item_oci
	and	a.nr_ordem_compra = nr_compra_p
	and	a.nr_item_oci = nr_seq_item_p
	and	b.nr_sequencia = nr_item_p;

	CALL gerar_ordem_compra_hist(nr_compra_p, null, WHEB_MENSAGEM_PCK.get_texto(297097),
				WHEB_MENSAGEM_PCK.get_texto(	297096,
						'CD_MATERIAL_W='||cd_material_w||
						';DS_MATERIAL_W='||ds_material_w||
						';DT_ENTREGA_SOLICITADA_W='||dt_entrega_solicitada_w||
						';QT_ENTREGA_SOLICITADA_OLD_W='||qt_entrega_solicitada_old_w||
						';QT_ENTREGA_SOLICITADA_NEW_W='||qt_entrega_solicitada_new_w),
				nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_quantidade_entrega ( nr_compra_p bigint, nr_seq_item_p bigint, nr_item_p bigint, qt_entrega_p bigint, ie_tipo_p text, nm_usuario_p text) FROM PUBLIC;

