CREATE OR REPLACE PROCEDURE alterar_rat_auditoria ( nr_seq_auditoria_p bigint, nr_rat_p text, dt_inclusao_rat_p timestamp, nr_seq_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inclusao_ant_w	auditoria_rat.dt_inclusao_ant%type;
nr_rat_anterior_w	auditoria_rat.nr_rat_anterior%type;
nr_seq_auditoria_w	auditoria_rat.nr_seq_auditoria%type;
nr_seq_empresa_ant_w	auditoria_rat.nr_seq_empresa_ant%type;



BEGIN

if (nr_seq_auditoria_p IS NOT NULL AND nr_seq_auditoria_p::text <> '') and (nr_rat_p IS NOT NULL AND nr_rat_p::text <> '') and (dt_inclusao_rat_p IS NOT NULL AND dt_inclusao_rat_p::text <> '') and (nr_seq_empresa_p IS NOT NULL AND nr_seq_empresa_p::text <> '') then

	select	dt_inclusao_rat,
		nr_rat,
		nr_seq_empresa
	into STRICT	dt_inclusao_ant_w,
	        nr_rat_anterior_w,
	        nr_seq_empresa_ant_w
	from	auditoria_conta_paciente
	where	nr_sequencia = nr_seq_auditoria_p;

	insert	into auditoria_rat(	dt_atualizacao,
					dt_atualizacao_nrec,
					dt_inclusao_ant,
					dt_inclusao_atual,
					nm_usuario,
					nm_usuario_nrec,
					nr_rat_anterior,
					nr_rat_atual,
					nr_seq_auditoria,
					nr_seq_empresa_ant,
					nr_seq_empresa_atual,
					nr_sequencia)
				values (	clock_timestamp(),
					clock_timestamp(),
					dt_inclusao_ant_w,
					dt_inclusao_rat_p,
					nm_usuario_p,
					nm_usuario_p,
					nr_rat_anterior_w,
					nr_rat_p,
					nr_seq_auditoria_p,
					nr_seq_empresa_ant_w,
					nr_seq_empresa_p,
					nextval('auditoria_rat_seq'));

	update	auditoria_conta_paciente
	set	nr_rat = nr_rat_p,
		dt_inclusao_rat = dt_inclusao_rat_p,
		nr_seq_empresa = nr_seq_empresa_p,
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_auditoria_p;

	commit;

end if;

end;
