CREATE OR REPLACE PROCEDURE alterar_resp_cobranca (nr_seq_cobrador_ant_p bigint, nr_seq_cobrador_novo_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cobrador_ant_w cobranca.nr_seq_cobrador%type;


BEGIN

if (coalesce(nr_seq_cobrador_ant_p::text, '') = '') then

  select max(nr_seq_cobrador) into STRICT nr_seq_cobrador_ant_w from cobranca where nr_sequencia = nr_sequencia_p;

else

  nr_seq_cobrador_ant_w := nr_seq_cobrador_ant_p;

end if;

insert into cobranca_alt_cobrador(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_cobranca,
			dt_alteracao,
			nr_seq_cobr_ant,
			nr_seq_cobr_novo,
			ds_observacao)
values (nextval('cobranca_alt_cobrador_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		clock_timestamp(),
		nr_seq_cobrador_ant_w,
		nr_seq_cobrador_novo_p,
		null);
commit;

end;
$body$
